<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@babel/plugin-transform-arrow-functions</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
